package com.example.bryan.tipeaze.Presets;import android.content.Context;import android.database.Cursor;import android.support.annotation.Nullable;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.CursorAdapter;import android.widget.TextView;import com.example.bryan.tipeaze.CustomViews.RobotoBasedTextview;import com.example.bryan.tipeaze.R;public class PresetAdapter extends CursorAdapter {    private static final String COL_NAME = PresetContract.TABLE_PRESET_NAMES.COL_NAME;    private int spinnerLayout = R.layout.spinner_layout;    private int spinnerDropDownLayout = R.layout.spinner_dropdown_layout;    private LayoutInflater layoutInflater;    public PresetAdapter(Context context, @Nullable Cursor cursor){        super(context, cursor, false);        layoutInflater = LayoutInflater.from(context);    }    @Override    public View newView(Context context, Cursor cursor, ViewGroup parent) {        /**         * FOR LEARNING PURPOSES:         *         *         *         * inflater.infate(int layoutResId, ViewGroup parent)         * inflater.inflate(int layoutResId, ViewGroup parent, boolean attachToRoot)         *         * "parent" is used to let a childs LayoutParams be respected         *         * "attachToRoot" should be false since the views returned here will be         * attached to the AdapterView<T> which is a subclass of ViewGroup         *         */        return layoutInflater.inflate(spinnerLayout, parent, false);    }    @Override    public View newDropDownView(Context context, Cursor cursor, ViewGroup parent) {        return layoutInflater.inflate(spinnerDropDownLayout, parent, false);    }    @Override    public void bindView(View view, Context context, Cursor cursor) {        ((TextView)view).setText(getNameFromCursor(cursor));    }    private String getNameFromCursor(Cursor cursor){        return cursor.getString(cursor.getColumnIndexOrThrow(COL_NAME));    }    public void setSpinnerLayout(int layoutId){        this.spinnerLayout = layoutId;    }    public void setSpinnerDropDownLayout(int layoutId){        this.spinnerDropDownLayout = layoutId;    }}