package com.example.bryan.tipeaze.Presets;import android.content.Context;import android.database.Cursor;import android.support.annotation.Nullable;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.CursorAdapter;import com.example.bryan.tipeaze.CustomViews.RobotoBasedTextview;import com.example.bryan.tipeaze.R;public class PresetAdapter extends CursorAdapter {    private static final String COL_NAME = PresetContract.TABLE_PRESET_NAMES.COL_NAME;    private int layoutRes;    private LayoutInflater layoutInflater;    public PresetAdapter(Context context, int layoutId,  @Nullable Cursor cursor){        super(context, cursor, false);        layoutRes = layoutId;        layoutInflater = LayoutInflater.from(context);    }    @Override    public View newView(Context context, Cursor cursor, ViewGroup parent) {        /**         * FOR LEARNING PURPOSES:         *         *         *         * inflater.infate(int layoutResId, ViewGroup parent)         * inflater.inflate(int layoutResId, ViewGroup parent, boolean attachToRoot)         *         * "parent" is used to let a childs LayoutParams be respected         *         * "attachToRoot" should be false since the views returned here will be         * attached to the AdapterView<T> which is a subclass of ViewGroup         *         */        final View newView = layoutInflater.inflate(layoutRes, parent, false);        return newView;    }    @Override    public void bindView(View view, Context context, Cursor cursor) {        ((RobotoBasedTextview)view.findViewById(R.id.presetTextViewId)).setText(                getNameFromCursor(cursor)        );    }    private String getNameFromCursor(Cursor cursor){        return cursor.getString(cursor.getColumnIndexOrThrow(COL_NAME));    }}